DROP TABLE IF EXISTS `chat_statistics`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `chat_statistics`  AS SELECT cast(`chat_logs`.`date_created` as date) AS `chat_date`, count(0) AS `total_chats`, count(distinct `chat_logs`.`session_id`) AS `unique_sessions`, `chat_logs`.`response_type` AS `response_type`, count(case when `chat_logs`.`bot_response` is null then 1 end) AS `no_answer_count` FROM `chat_logs` GROUP BY cast(`chat_logs`.`date_created` as date), `chat_logs`.`response_type` ;

