-- Structure for view `popular_questions`
--
DROP TABLE IF EXISTS `popular_questions`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `popular_questions`  AS SELECT `chat_logs`.`user_message` AS `user_message`, count(0) AS `frequency`, `chat_logs`.`response_type` AS `response_type`, max(`chat_logs`.`date_created`) AS `last_asked` FROM `chat_logs` WHERE octet_length(`chat_logs`.`user_message`) > 3 GROUP BY `chat_logs`.`user_message`, `chat_logs`.`response_type` ORDER BY count(0) DESC LIMIT 0, 50 ;

